INSERT INTO `statement` (`idstatement`, `dates`, `idclent`, `namework`, `daterin`, `daterout`, `desc`, `summa`, `srokkredita`, `pervonzvon`, `countteam`, `idmanager`, `resoluciy`, `statuswork`, `dateof`) VALUES
	(1, '2023-09-22', 2, 'Polycam', '2023-09-22', '2023-09-22', 'Это технология на базе искусственного интеллекта, которая позволяет пользователям захватывать и делиться своим миром в 3D, используя видео LiDAR и данные о движении, доступные на смартфонах. С помощью приложения Polycam пользователи могут превращать обычные фотографии в детализированные, реалистичные творения, что делает его инновационным инструментом для профессионалов в области архитектуры, дизайна интерьера и моды. Приложение также подходит для повседневного использования и поддерживается известными венчурными компаниями, такими как Sequoia Capital и Chad Hurley. Polycam доступен для iOS, Android и веб, может использоваться в различных профессиональных 3D-процессах, включая планы этажей, архитектурные визуализации, визуальные эффекты и кинопроизводство.', 677890, 13, 5000000, '5', 2, 'в процессе', '3', '2023-09-22'),
	(2, '2023-09-22', 3, 'Photon Commerce', '2023-09-22', '2023-09-22', 'Фотонная коммерция— это финансовая платформа искусственного интеллекта, которая позволяет финтех-компаниям обрабатывать платежи B2B, счета, выписки, контракты и другие документы со сверхчеловеческой скоростью и с высокой точностью. Более 120 компаний в области финансовых технологий, электронной коммерции и логистики используют эту технологию, чтобы лучше понимать своих клиентов и взаимодействовать с ними, а Photon обработал более 23 миллиардов долларов данных о платежах и счетах-фактурах со своими клиентами и партнерами. Технология искусственного интеллекта Photon позволяет компаниям собирать, настраивать и использовать сложные данные, чтобы понимать своих клиентов на более глубоком уровне, чем когда-либо прежде.', 890484, 10, 600000, '4', 4, 'заключительный этап', '3', '2023-09-22'),
	(3, '2023-09-22', 1, 'CloudAEye', '2023-09-22', '2023-09-22', 'ОблакоAEye — это отличное решение искусственного интеллекта для микросервисов. Они создали сложную технологию на основе искусственного интеллекта, которая обеспечивает сквозную видимость бессерверных приложений. Команда из Сан-Франциско осознала важность времени выхода на рынок и надежности обслуживания в современном удаленном мире, особенно с учетом того, что, по данным IDC, 29,3% предприятий сталкиваются с простоями, стоимость которых превышает 25 тысяч долларов в час. Технология CloudAEye включает в себя модели на основе ИИ, которые используют интеллектуальный мониторинг для анализа данных приложений, выявления аномалий и предоставления подробного анализа первопричин каждого инцидента, что позволяет клиентам быстро понять и устранить проблемы.', 3324440, 12, 99000, '2', 3, 'начало', '3', '2023-09-22');

