INSERT INTO `vidi` (`idvidi`, `name`, `desc`, `detail`) VALUES
	(1, 'Государственная ITподдержка', 'это программа субсидирования процентных ставок по кредитам на покупку жилья: государство помогает отдельным категориям граждан приобрести собственную недвижимость в ипотеку по сниженной процентной ставке', 'Первоначальный взнос от 15%\r\nСрок кредита до 30 лет\r\nСумма кредита до 6 млн ₽\r\nИпотека с господдержкой от 7,3%'),
	(2, 'IT Student kredit', 'Участники этой программы могут взять ипотеку или рефинансировать уже имеющийся кредит по льготной ставке — 6%. Разницу между обычной и льготной ставкой банку компенсирует государство.', 'Первоначальный взнос от 15%\r\nСрок кредита до 30 лет\r\nСумма кредита до 6 млн ₽\r\nИпотека с господдержкой от 7,3%'),
	(3, 'Социальный It кредит', 'Участники этой программы могут взять ипотеку или рефинансировать уже имеющийся кредит по льготной ставке — 6%. Разницу между обычной и льготной ставкой банку компенсирует государство.', 'Первоначальный взнос от 15%\r\nСрок кредита до 30 лет\r\nСумма кредита до 6 млн ₽\r\nИпотека с господдержкой от 7,3%'),
	(4, 'Бизнес для молодых IT специалистов', 'это льготная программа, принятая после постановления Правительства России в марте 2022 года о поддержке сотрудников компаний, осуществляющих деятельность в сфере информационных технологий. Господдержка позволяет получить ипотечный кредит с пониженной процентной ставкой до 5% годовых на жилой дом или квартиру от застройщика в готовой или строящейся новостройке.', 'Первоначальный взнос от 15%\r\nСрок кредита до 30 лет\r\nСумма кредита до 6 млн ₽\r\nИпотека с господдержкой от 7,3%');

